import Meta from '../Meta/Meta';
import Sidebar from '../Sidebar/Sidebar';
import MainContent from '../MainContent/MainContent';
import ProfileForm from '../ProfileForm/ProfileForm';

const Dashboard = ({pageTitle, route, logout})=> {
    let componentToRender;
    switch (route) {
        case "main":
            componentToRender = <p>I'm the main</p>;
            break;

        case "profile":
                componentToRender = (
                    <div className="app__main--content">>
                        <h1 className="app__main--details">Hi</h1>
                        <div className="app__main--details">
                            <ProfileForm/>
                        </div>
                    </div>
                );
                break;

        case "settings":
            componentToRender = <h1>Settings page</h1>;
            break;

        default:
            componentToRender = <p>Page not found</p>;
            break;
    }

    return (
        <div>
            <Meta pageTitle={pageTitle}/>
            <div className="app">
                <Sidebar logout={logout}/>
                <MainContent>
                    {componentToRender}
                </MainContent>
                <style jsx>
                {`  
                    .app__main{
                        background-color: #fefcf5;
                        overflow-y: scroll;
                        flex: 0 0 80%;
                        padding: 5rem 0;
                        /* display: flex;
                        flex-direction: column;
                        align-items: center;
                        justify-content: space-around; */
                    }

                    .app__main--content{
                        width: 80%;
                        margin: 0 auto;
                    }

                    .app__main--content:not(:last-child){
                        background-color: pink;
                        margin-bottom: 5rem;
                    }

                    .app__main--listing{
                        display: flex;
                        justify-content: space-between;
                        align-items: center;
                        color: #683507;
                        /* background-color: blueviolet; */
                    }

                    .app__main--details{
                        display: flex;
                        justify-content: space-between;
                    }

                    .app__main--details:not(:last-child){
                        margin-bottom: 5rem;
                    }

                    /* .app__main--details > *{ 
                        background-color: green;
                    } */
                `}
            </style>
            </div>
        </div>
    )
};

export default Dashboard;
