import { Component } from 'react';
import { createStore, combineReducers } from 'redux';
import { Provider } from 'react-redux';
import { routeReducer, bankReducers } from '../reducers/reducers';
import Auth from '../components/Auth/Auth';
import Dashboard from '../components/Dashboard/Dashboard';

const rootReducer = combineReducers({routeReducer, bankReducers});
const store = createStore(rootReducer);

class App extends Component{
    constructor(props){
        super(props)

        this.state = {
            user : true
        }
    }

    logOut = ()=>{
        this.setState({user: false});
    }

    render(){
        return (
            <Provider store={store}>
                {(this.state.user) ? <Dashboard pageTitle="Dashboard - EazyRent" route="main" logout={this.logOut}/> : <Auth/>}
            </Provider>
        );
    }
}

export default App;
