import Meta from '../Meta/Meta';
import Sidebar from '../Sidebar/Sidebar';
import MainContent from '../MainContent/MainContent';
import UserAvatar from '../UserAvatar/UserAvatar';
import ProfileForm from '../ProfileForm/ProfileForm';
import ProfileBox from '../ProfileBox/ProfileBox';

const Dashboard = ({pageTitle, route, logout})=> {
    let componentToRender;
    switch (route) {
        case "main":
            componentToRender = <p>I'm the main</p>;
            break;

        case "profile":
                componentToRender = (
                    <div>
                        <div className="app__main--details"><ProfileBox/></div>
                        <div className="app__main--details"><ProfileForm/></div>
                    </div>
                );
                break;

        case "settings":
            componentToRender = <h1>Settings page</h1>;
            break;

        default:
            componentToRender = <p>Page not found</p>;
            break;
    }

    return (
        <div>
            <Meta pageTitle={pageTitle}/>
            <div className="app">
                <Sidebar logout={logout}/>
                <MainContent>
                    {componentToRender}
                </MainContent>
                <style jsx>
                    {`
                        .app{
                            width: 100%;
                            height: 100vh;
                            display: flex;
                        }

                        .app > *{
                            height: inherit;
                        }

                        .app__main--details{
                            background-color: olive
                            display: flex;
                            justify-content: space-between;
                        }

                        .app__main--details:not(:last-child){
                            margin-bottom: 5rem;
                        }

                        /* .app__main--details > *{ 
                            background-color: green;
                        } */
                    `}
                </style>
            </div>
        </div>
    )
};

export default Dashboard;
