import { useState, useEffect } from 'react';
import { Bank } from '../types/types';
import Dashboard from '../components/Dashboard/Dashboard';
import Cookie from 'js-cookie';
import cookie from 'next-cookies';

const payment = ({initialBanks})=> {
    const [banks, bankStatus] = useState((initialBanks.length === 0) ? [] : initialBanks);
    const [message, setAlertMessage] = useState('');

    useEffect(()=>{
        Cookie.set('banks', banks);
    });

    const showAlert = (newMessage) =>{
        setAlertMessage(newMessage);
    }

    const addNewBank = (newBank) =>{
        if(banks.length >= 1){
            for (const bank of banks) {
                if(bank.account === newBank.account){
                    setAlertMessage('Bank already added');
                }else{
                    bankStatus([...banks, newBank]);
                }
            }
        }else{
            bankStatus([...banks, newBank]);
        }
    }

    const deleteBank = (id) =>{
        banks.splice(id, 1);
        bankStatus([...banks]);
    }

    const [form, changeForm] = useState('');

    const changeActiveForm = (newForm) => {
        changeForm(newForm);
    }

    return <Dashboard pageTitle="Payments - EazyRent" route="payment" banks={banks} addBank={addNewBank} deleteBank={deleteBank} form={form} changeForm={changeActiveForm} alert={message} showAlert={showAlert}/>;
};

payment.getInitialProps = (ctx)=>{
    const cookies = cookie(ctx);
    return {
        initialBanks: cookies.banks
    };
}

export default payment;
