import { connect } from 'react-redux';
import Meta from '../Meta/Meta';
import Sidebar from '../Sidebar/Sidebar';
import MainContent from '../MainContent/MainContent';
import ProfileForm from '../ProfileForm/ProfileForm';
import ProfileBox from '../ProfileBox/ProfileBox';
import { addToBank, changeForm, resetAlertBox, retrieveState, editBank, overwriteBank, deleteBank } from '../../actions/actions';
import { loadGetInitialProps } from 'next/dist/next-server/lib/utils';


const Dashboard = ({pageTitle, route})=> {
    let componentToRender;
    switch (route) {
        case "main":
            componentToRender = <p>I'm the main</p>;
            break;

        case "profile":
            componentToRender = (
                <div>
                    <ProfileBox/>
                    <ProfileForm/>
                </div>
            );
            break;

        case "settings":
            componentToRender = <h1>Settings page</h1>;
            break;

        case "payment":
            componentToRender = <h1>Payment page {loadGetInitialProps.banks} </h1>;
            break;
    }

    return (
        <div>
            <Meta pageTitle={pageTitle}/>
            <div className="app">
                <Sidebar route={route}/>
                <MainContent>
                    {componentToRender}
                </MainContent>
                <style jsx>
                    {`
                        .app{
                            width: 100%;
                            height: 100vh;
                            display: flex;
                        }

                        .app > *{
                            height: inherit;
                        }
                    `}
                </style>
            </div>
        </div>
    )
};

Dashboard.getInitialProps = async({store})=>{
    store.dispatch(addToBank, changeForm, resetAlertBox, retrieveState, editBank, overwriteBank, deleteBank);
    return {
        banks: "Banks"
    };
}

export default connect(state => state)(Dashboard);
