import { useState } from 'react';
import { Bank } from '../types/types';
import Dashboard from '../components/Dashboard/Dashboard';

const payment = ()=> {
    const [banks, bankStatus] = useState([]);

    const addNewBank = (newBank) =>{
        bankStatus([...banks, newBank]);
        localStorage.setItem('banks', JSON.stringify(banks));
        //new Bank(1234, 'GT Bank', '2357810094', 'current', '256092341625', 'verified')  
    }

    const deleteBank = (id) =>{
        banks.splice(id, 1);
        bankStatus([...banks])
    }

    const [form, changeForm] = useState('');

    const changeActiveForm = (newForm) => {
        changeForm(newForm);
    }

    return <Dashboard pageTitle="Payments - EazyRent" route="payment" banks={banks} addBank={addNewBank} deleteBank={deleteBank} form={form} changeForm={changeActiveForm}/>;
};

payment.getInitialProps = ()=>{
    console.log(JSON.parse(localStorage.getItem('banks')));
    console.log('Hi');

}

export default payment;
