const WelcomeBox = ()=>{
    return (
        <div>
            <div className="welcome-box">
                <h3 className="welcome-box__text">
                    <span className="welcome-box__text--title">Hi, Scarlett</span>
                    <span className="welcome-box__text--subtitle">what would you like to do today?</span>
                </h3>
                <img src="/img/Finance.png" className="welcome-box__illustration"/>
                <span className="welcome-box__boxes"></span>
            </div>
            <style jsx>
                {`
                    .welcome-box{
                        height: 25rem;
                        width: 100%;
                        border-radius: 2rem;
                        background-color: #FEECC8;
                        overflow: hidden;
                        position: relative;
                    }

                    .welcome-box__text{
                        position: absolute;
                        left: 5rem;
                        top: 45%;
                        transform: translateY(-50%);
                        width: 50%;
                        color: #C45A00;
                    }

                    .welcome-box__text--title{
                        display: block;
                        font-size: 5rem;
                    }

                    .welcome-box__text--subtitle{
                        // letter-spacing: .5rem;
                        font-size: 1.5rem;
                        text-transform: capitalize;
                    }

                    .welcome-box__illustration{
                        position: absolute;
                        top: -.5rem;
                        right: .5rem;
                        width: 45%
                    }
                `}
            </style>
        </div>
    );
}

export default WelcomeBox;
