import Dashboard from '../components/Dashboard/Dashboard';

const addlisting = ()=>{
    const submitForm = async()=>{
        // let formData = new FormData();
        // formData.append('title', document.getElementById('title').value);
        // formData.append('address', document.getElementById('address').value);
        // formData.append('price', document.getElementById('price').value);
        // formData.append('payment', document.getElementById('payment').value);
        // formData.append('city', document.getElementById('city').value);
        // formData.append('state', document.getElementById('state').value);
        // formData.append('beds', document.getElementById('beds').value);
        // formData.append('baths', document.getElementById('baths').value);
        // formData.append('images', document.getElementById('images').files[0]);
        const images = Array.from(document.getElementById('images').files);
        const imagesToBeUploaded = [];

        images.forEach(
            (v, i)=>{
                const reader = new FileReader();
                reader.readAsDataURL(images[i]);
                console.log(reader.result);
                imagesToBeUploaded.push(reader.result);
            }
        );

        const dataToPost = {
            user: "abgfigu143668",
            name : document.getElementById('title').value,
            address: document.getElementById('address').value,
            price : document.getElementById('price').value,
            type : document.getElementById('payment').value,
            city : document.getElementById('city').value,
            state : document.getElementById('state').value,
            beds : document.getElementById('beds').value,
            baths : document.getElementById('baths').value,
            images : imagesToBeUploaded
        }

        const res = await fetch('http://localhost:3004/listings', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(dataToPost)
        });
        const data = await res.json();
        console.log('Success', JSON.stringify(data));
    }

    // const getImages = async(files)=>{
    //     console.log(files);

    //     let formData = new FormData();
    //     formData.append('images', files[0]);

    //     const res = await fetch('http://localhost:3004/users', {
    //         method: 'POST',
    //         body: formData
    //     });
    //     const data = await res.json();
    //     console.log('Image uploaded', JSON.stringify(data));


    // }

    return <Dashboard route="add-listing" addListing={submitForm}/>;
}

export default addlisting;
