const images = ['/img/home-1.jpg', '/img/home-2.jpg', '/img/home-3.jpg', '/img/home-4.jpg', '/img/home-5.jpg', '/img/home-6.jpg', '/img/home-7.jpg', '/img/home-8.jpg'];
class ImageGallery extends Component{
    constructor(){
        super();
        this.state = {
            images : images,
            activeImage: images[0]
        }
    }

    changeActiveImage (image){
        this.setState({activeImage: image});
    }

    render(){
        return (
            <div>
                <div className="image-gallery">
                    <img alt="mainImage" className="image-gallery__active" src={this.state.activeImage}/>
                    <div className="image-gallery__inactive">
                        {this.state.images.map(
                            (v, i) => {
                                if(images[i] === this.state.activeImage){
                                    return <img key={images[i]} alt="activeImage" className="image-gallery__inactive--images image-gallery__inactive--images--active" src={images[i]} onClick={()=> this.changeActiveImage(images[i])}/>
                                }else{
                                    return <img key={images[i]} alt="inactiveImage" className="image-gallery__inactive--images" src={images[i]} onClick={()=> this.changeActiveImage(images[i])}/>
                                }
                            }
                        )}
                    </div>
                </div>
                <style jsx>
                    {`
                        .image-gallery{
                            width: 45%;
                            min-height: 30rem;
                            display: flex;
                            flex-direction: column;
                            justify-content: space-between;
                        }

                        .image-gallery__active{
                            height: 20rem;
                            width: 100%;
                            border-radius: 1rem;
                            margin-bottom: 2rem;
                            cursor: pointer;
                        }

                        .image-gallery__inactive{
                            width: 100%;
                            /* min-height: 12rem; */
                            display: flex;
                            flex-wrap: wrap;
                            justify-content: space-between;
                            align-content: space-around;
                        }

                        .image-gallery__inactive--images{
                            width: 7.5rem;
                            border-radius: .5rem;
                            margin-bottom: 1rem;
                            cursor: pointer;
                        }

                        .image-gallery__inactive--images--active{
                            outline: .2rem solid #f2994a;
                            outline-offset: .2rem;
                        }

                        .image-gallery__inactive--images:hover{
                            outline: .2rem solid #f2994a;
                            outline-offset: .2rem;
                        }

                        /* .image-gallery__inactive--images{
                            height: 7.5rem;
                        } */
                    `}
                </style>
            </div>
        );
    }
}

export default ImageGallery;
